import React, { Component } from "react";
import { FiCast, FiLayers, FiUsers, FiMonitor } from "react-icons/fi";
import { FaEye, FaGithubSquare } from "react-icons/fa";

const ServiceList = [
  {
    icon: (
      <img src="/assets/images/portfolio/portfolio-1.png" alt="PayitForward" />
    ),
    title: "PayItForward",
    description:
      "I throw myself down among the tall grass by the stream as I lie close to the earth.",
    buttonText1: "",
    buttonLink1: "",
    buttonText2: "",
    buttonLink2: "",
  },
  {
    icon: (
      <img
        src="/assets/images/portfolio/dailygratitude.png"
        alt="PayitForward"
      />
    ),
    title: "Daily Gratitude",
    description:
      "I throw myself down among the tall grass by the stream as I lie close to the earth. This is to see how the card will adjust depending on the type of text and how long the text is",
  },
  {
    icon: <FiUsers />,
    title: "Marketing & Reporting",
    description:
      "I throw myself down among the tall grass by the stream as I lie close to the earth.",
  },
];

class ServiceThree extends Component {
  render() {
    const { column } = this.props;
    const ServiceContent = ServiceList.slice(0, this.props.item);

    return (
      <React.Fragment>
        <div className="row service-main-wrapper">
          {ServiceContent.map((val, i) => (
            <div className={`${column}`} key={i}>
              <a href="/service-details">
                <div className="service service__style--2 text-left bg-gray">
                  <div className="icon">{val.icon}</div>
                  <div className="content">
                    <h3 className="title">{val.title}</h3>
                    <p>{val.description}</p>
                    {val.buttonText ? (
                      <div className="slide-btn">
                        <a className="btn-default" href={`${val.buttonLink}`}>
                          {val.buttonText}
                        </a>
                      </div>
                    ) : (
                      ""
                    )}
                    {val.buttonText ? (
                      <div className="slide-btn">
                        <a className="btn-default" href={`${val.buttonLink}`}>
                          {val.buttonText}
                        </a>
                      </div>
                    ) : (
                      ""
                    )}
                  </div>
                </div>
              </a>
            </div>
          ))}
        </div>
      </React.Fragment>
    );
  }
}
export default ServiceThree;
